<?phpinclude_once dirname(__FILE__)."/shared/ez_sql_core.php";include_once dirname(__FILE__)."/ez_sql_mysqli.php";define("EW_CONN_HOST", 'localhost', TRUE);define("EW_CONN_PORT", 3306, TRUE);define("EW_CONN_USER", 'root', TRUE);define("EW_CONN_PASS", 'root', TRUE);define("EW_CONN_DB", '', TRUE);$db = new ezSQL_mysql(EW_CONN_USER,EW_CONN_PASS,EW_CONN_DB,EW_CONN_HOST.':'.EW_CONN_PORT);$db->query("set names utf8");//随机生成一个订单号($pre，订单前缀)function order_sn($pre = ''){	$order_sn = $pre.date('ymd').substr(time(),-5).substr(microtime(),2,5).rand(1000,9999);	return $order_sn;}function insert_arr($arr,$table_name){global $db;	if(!empty($arr)){	//先获取表的名称	$sql = "describe $table_name";	$table_list = $db->get_results($sql);	$t1 = '';	$t2 = '';	foreach($arr as $k=>$v){	foreach($table_list as $table_user){	if($table_user->Field == $k){	if($t1==""){	$t1 = '`'.$k.'`';	}else{	$t1 = $t1 . ',`'.$k.'`';	}	if($t2==""){	$t2 = '"'.addslashes($v).'"';	}else{	$t2 = $t2.',"'.addslashes($v).'"';	}	break;	}	}	}	$sql = "insert into `$table_name`($t1) values($t2)";	$re1 = $db->Query($sql);	return $re1;	}else{	return 0;	}}function update_arr2($arr,$table_name,$where){    global $db;    if(!empty($arr)){        //先获取表的名称        $sql = "describe $table_name";        $table_list = $db->get_results($sql);        $t1 = '';        $t2 = '';        $t3 = '';        $key_zd = ''; //Key字段        $key_value = ''; //Key值		$sql_mul = ''; //联合主键sql语句						        foreach($arr as $k=>$v){            foreach($table_list as $table_user){                if($table_user->Field == $k){                    $t1 = $k;					$t2 = addslashes($v);					$t3 .= " `{$t1}` = '{$t2}',";                }            }        }        $t3 = trim($t3,',');				$sql = "update `$table_name` set {$t3} where {$where} ";		@$re1 = $db->Query($sql);		if($re1){			return $re1;		}else{			return 0;		}    }else{        return 0;    }}function select_arr($field,$table_name,$where){	global $db;	$sql = "select {$field} from `{$table_name}` where {$where}";	$users = $db->get_results($sql);	$arr = array();	if(!empty($users)){		$arr = json_decode(json_encode($users),true);	}	return $arr;	}?>